import { createContext, useState } from "react";


const FavoritesContext = createContext({
    favorites: [],
    totalFavorites: 0,
    addFavorite: "",
    removeFavorite: "",
    itemIsFavorite: ""

});

export function FavoritesContextProvider(props) {
    const [userFavorites, setUserFavorites] = useState([])

    function addFavoriteHandler(url) {
        setUserFavorites((prevState) => [{ url: url.url, title: url.title }, ...prevState])
    }

    function removeFavoriteHandler(url) {
        setUserFavorites((prevState) => prevState.filter(key => key.url !== url))
    }

    function itemIsFavoriteHandler(url) {
        return userFavorites.some(key => key.url === url)
    }



    const context = {
        favorites: userFavorites,
        totalFavorites: userFavorites.length,
        addFavorite: addFavoriteHandler,
        removeFavorite: removeFavoriteHandler,
        itemIsFavorite: itemIsFavoriteHandler

    }


    return <FavoritesContext.Provider value={context}>
        {props.children}

    </FavoritesContext.Provider>
}

export default FavoritesContext



