import logo from './logo.svg';
import './App.css';
import SongList from './components/SongList/SongList.js';
import react from 'react';
import { useRef, useState } from 'react'
import ReactPlayer from 'react-player'

function App() {
  const reffer = useRef("bla bla")

  let temp = ""
  const [song, setSong] = useState("")
  const [input, setInput] = useState("")
  const [songList, setSongList] = useState([{ id: 1, name: "song1", artist: "artist" },
  { id: 2, name: "song2", artist: "artist" },
  { id: 3, name: "song3", artist: "artist" },
  { id: 4, name: "song4", artist: "artist" }])

  function inputHandler(event) {
    setInput(event.target.value)
    // songList.push(event.target.value)
  }

  function clickHandler() {
    setSong(input)
    setSongList((prevState) => [...prevState, { id: 7, name: reffer.current.value, artist: "artist" }])
    setInput("")

  }





  return (
    <div className="App">
      {/* <Header /> */}
      {/* <AddItemForm /> */}
      <h1>Play List</h1>
      <div className='searchInterface'>
        <input className="input" onChange={inputHandler} ref={reffer} value={input}></input>

        <div className="click" onClick={clickHandler}>Add</div>
      </div>
      <SongList songz={songList} />
      <ReactPlayer className="player"
        url="https://youtu.be/luai0p0y2zE"
        controls
        playbackRate={2}
        // width="100%"
        height="504px"
      />
      {/* <div>{song}</div> */}

    </div>
  );
}

export default App;
