import { useState, useRef } from "react"
import "./Login.css"

function Login() {
    const [login, setLogin] = useState(false);
    const userNameInput = useRef("")
    const passwordInput = useRef("")
    const [userLogged, setUserLogged] = useState("Guest")

    function signUpHandler() {
        setLogin(!login);

    }
    // function click(e) {
    //     console.log(e.target.value);

    // }

    function signInHandler() {
        console.log(userNameInput.current, passwordInput.current.value);
        fetch(`http://localhost:3001/users/register`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username: userNameInput.current.value, password: passwordInput.current.value })
        }).then((response) => (response).json()).then((data) =>
            setUserLogged(data.username))


    }

    function loginHandler() {
        console.log("login");

    }



    return <><div className="credentialsInterface">
        <div className="signUp" onClick={signUpHandler}>
            Signup
        </div>
        <div className="LoginMenu">
            {login ? (
                <>
                    <input className="userName" placeholder={"user name"} ref={userNameInput} className="usernameInput"></input>
                    <input placeholder={"password"} className="usernameInput" ref={passwordInput}></input>
                    <div onClick={signInHandler}>Register</div>
                </>
            ) : (
                <div></div>
            )}
        </div>
        <div className="Login" onClick={loginHandler}>
            Login
        </div>
        <div className="userLogged">hello {userLogged}</div></div>
    </>
}

export default Login
