import "./AllFavorites.css"
import Header from "../../components/Header/Header"
import Player from "../../components/Player/Player"
import Favorites from "../../components/Favorites/Favorites"
import { useContext, useState } from "react"
import FavoritesContext from '../../store/Favorites-context';


function AllFavorites() {
    const [currentUrl, setCurrentUrl] = useState("")
    const favoritesCtx = useContext(FavoritesContext);
    console.log(favoritesCtx.favorites);

    // function removeFavoriteHandler(e, url) {
    //     console.log(url);
    //     console.log(currentUrl);

    // }

    return <div className="FavoritesPageWrapper">
        <Header />
        <div className="Tester">
            All favorites page
            <div className='Container'>



                {favoritesCtx.favorites.map(url => {
                    return <div onClick={() => { favoritesCtx.removeFavorite(url.url) }}>Remove favorite <br />
                        <div key={url.url}>

                            {<Player url={`https://www.youtube.com/watch?v=${url.url}`} width="95%" height="30vh" playing={false} />

                            }</div>
                        <p>{url.title}</p>
                    </div>
                })}




            </div>

        </div></div>
}

export default AllFavorites;

