import logo from './logo.svg';
import './App.css';
import SongList from './components/SongList/SongList.js';
import react from 'react';
import { useRef, useState } from 'react'

function App() {

  const songList = [{ id: 1, name: "song1", artist: "artist" },
  { id: 2, name: "song1", artist: "artist" },
  { id: 3, name: "song1", artist: "artist" },
  { id: 4, name: "song1", artist: "artist" }]

  const [song, setSong] = useState("")
  const [input, setInput] = useState("")


  function inputHandler(event) {
    setInput(event.target.value)
    // songList.push(event.target.value)
  }

  function clickHandler() {
    setSong(input)
    setInput("")

  }





  return (
    <div className="App">
      {/* <Header /> */}
      {/* <AddItemForm /> */}
      <input onChange={inputHandler} value={input}></input>
      <button onClick={clickHandler}></button>
      <SongList songz={songList} />
      <div>{song}</div>


    </div>
  );
}

export default App;
