import logo from './logo.svg';
import './App.css';
import SongList from './components/SongList/SongList.js';
import react from 'react';
import { useRef, useState } from 'react'
import ReactPlayer from 'react-player/youtube'
import Player from './components/Player/Player';
import YTSearch from 'youtube-api-search'
import Favorites from './components/Favorites/Favorites';


// AIzaSyBFzJq7uzn3s8IACnWT0Fum0sJ4NxPZaT0


function App() {
  const reffer = useRef("adelle")
  // const blue = useRef("")
  function videoSearch(term) {
    YTSearch({ key: "AIzaSyDkzCPPFU6hoflI7xIVOcKLZjgNDLvaT4A", term: term },
      (videos) => {
        setTimeout(() => {
          setUrl(videos[0].id.videoId)

        }, 1000);
      }
    )
  }

  // videoSearch("adelle")
  // videoSearch("our last night ")
  const [url, setUrl] = useState("uc7_YZYCCAI")

  videoSearch("adelle")

  const [song, setSong] = useState("")
  const [input, setInput] = useState("")
  const [songList, setSongList] = useState([{ id: 1, name: "song1", artist: "artist" },
  { id: 2, name: "song2", artist: "artist" },
  { id: 3, name: "song3", artist: "artist" },
  { id: 4, name: "song4", artist: "artist" }])

  function inputHandler(event) {
    setInput(event.target.value)
    console.log("input", reffer.current.value);
    // songList.push(event.target.value)
  }

  function clickHandler() {
    // setSong(input)
    // setSongList((prevState) => [...prevState, { id: 7, name: reffer.current.value, artist: "artist" }])
    setInput("")
    console.log(reffer.current.value);
    videoSearch("adelle")




  }






  return (

    <div className='wrapper'>
      <div className="App">
        {/* <Header /> */}
        {/* <AddItemForm /> */}
        <h1>MusiXX</h1>
        <Player url={`https://www.youtube.com/watch?v=${url}`} width="100%" playing={true} />
        <div className='searchInterface'>
          <form>
            <input className="input" onChange={inputHandler}
              ref={reffer} value={input} placeholder='artist name' />
            {/* <input className="input" onChange={inputHandler}
              ref={reffer} value={input} placeholder='song name' />
            <input className="input" onChange={inputHandler}
              ref={reffer} value={input} placeholder='random lyrics' /> */}

            <button className="click" type='submit' onClick={clickHandler}>Search</button>
          </form>


        </div>
        <h2>favorites</h2>

        <div className='favorites-app'>
          <Favorites url={`https://www.youtube.com/watch?v=${"ptwt7Fs5INo"}`} width="38vh" height="40vh" playing={true} />
          <Favorites url={`https://www.youtube.com/watch?v=${"l0yBFGZQYo0"}`} width="38vh" height="40vh" playing={true} />
          <Favorites url={`https://www.youtube.com/watch?v=${"PhdhxvduVVA&list=RDPhdhxvduVVA&start_radio=1"}`} width="38vh" height="40vh" playing={true} />

        </div>
        <SongList songz={songList} />


      </div>
    </div>
  );
}

export default App;
